mapper.select:
/* 게시글 조회 */ SELECT LIST2.* FROM ( SELECT LIST1.* FROM ( SELECT ROW_NUMBER() over (order by REG_DATE DESC) as no , BOARD_SNO , TITLE , CONTENT , USER_ID , REG_DATE FROM BOARD_INFO A
A.USE_YN = 'Y'
AND A.TITLE LIKE '%' || #{keyword} || '%'
AND A.CONTENT LIKE '%' || #{keyword} || '%'
AND A.USER_ID LIKE '%' || #{keyword} || '%'
) List1 WHERE LIST1.no <= #{lastRecordIndex} ) LIST2 WHERE LIST2.no >= #{firstRecordIndex}

/* 게시글 카운트 */ SELECT COUNT(*) FROM ( SELECT ROW_NUMBER() over (order by REG_DATE DESC) as no , BOARD_SNO boardSno , TITLE AS title , CONTENT as content , USER_ID as userId , REG_DATE as regDate FROM BOARD_INFO A
A.USE_YN = 'Y'
)

mapper.insert:
/* 게시글 등록/수정 */
MERGE INTO BOARD_INFO A
    /* 비교 조건(boardSno가 같은지 비교) */
    USING DUAL ON(A.BOARD_SNO = #{boardSno})
WHEN MATCHED THEN
/* update(boardSno가 일치하면 update) */
UPDATE


        TITLE = #{title}


        , CONTENT = #{content}

        , MOD_DATE = SYSDATE

 WHERE BOARD_SNO = #{boardSno}
/* insert(boardSno가 불일치 할 경우 insert) */
WHEN NOT MATCHED THEN
INSERT
        (
            BOARD_SNO
          , TITLE
          , CONTENT
          , USER_ID
        )
    VALUES
        (
            #{boardSno}
          , #{title}
          , #{content}
          , #{userId}
        )

mapper.update:
/* 게시글 삭제 */
UPDATE BOARD_INFO
   SET USE_YN = 'N'
 WHERE BOARD_SNO = #{boardSno}
