mapper.insert:

    SELECT USER_SNO_SEQ.NEXTVAL FROM DUAL

/* 사용자 정보 저장 */
INSERT INTO USER_INFO
    (
        USER_SNO                 /* 사용자 순번 */
      , USER_ID                 /* 사용자 아이디 */
      , USER_PASSWORD           /* 사용자 비밀번호 */
      , USER_NAME               /* 사용자명 */
      , USER_EMAIL              /* 사용자 이메일 */
      , REG_DATE                /* 등록일 */
    )
VALUES
    (
        #{userSno}
      , #{userId}
      , #{userPassword}
      , #{userName}
      , #{userEmail}
      , SYSDATE
    )

mapper.select:
/* 아이디로 카운트 조회 */
SELECT COUNT(*)
  FROM USER_INFO
 WHERE USER_ID = #{userId}

mapper.update:
/* 사용자 정보 수정 */
UPDATE USER_INFO
   SET USER_PASSWORD = #{userPassword}
     , USER_EMAIL = #{userEmail}
     , MOD_DATE = SYSDATE
 WHERE USER_SNO = #{userSno}
   AND USER_ID = #{userId}

/* 사용자 삭제 */
UPDATE USER_INFO
   SET USE_YN = 'N'
     , MOD_DATE = SYSDATE
 WHERE USER_SNO = #{userSno}
   AND USER_ID = #{userId}
